mapper.select:
select count(*) from
where ref_member_id = #{memberId}
where ( ref_member_id = #{memberId} and counsel_level = 1 ) or ( counsel_level = 2 and counsel_q_no in (select counsel_no from counsel where ref_member_id = #{memberId}) )

select count(*)
from counsel
where counsel_q_no = #{counselNo}
